package Model;

public class Taille {

	private int id;
	private double ratio;
	
	public Taille(int id, double ratio) {
		// TODO Auto-generated constructor stub
		this.id = id;
		this.ratio = ratio;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public double getRatio() {
		return ratio;
	}

	public void setRatio(double ratio) {
		this.ratio = ratio;
	}
}
